mapper.select:
SELECT
u.user_id,u.username,u.tel,a.article_id,a.cover_image,a.is_show,a.add_time
FROM t_user u INNER JOIN t_article a on u.user_id=a.user_id


    and user_id = #{userId}


    and username = #{username}


    and tel = #{tel}


    and article_id = #{articleId}


    and cover_image = #{coverImage}


    and is_show = #{isShow}


    and add_time = #{addTime}


order by add_time desc

mapper.insert:
insert into t_article (article_id, user_id, cover_image, 
  is_show, add_time, content
  )
values (#{articleId,jdbcType=INTEGER}, #{userId,jdbcType=INTEGER}, #{coverImage,jdbcType=VARCHAR}, 
  #{isShow,jdbcType=TINYINT}, #{addTime,jdbcType=TIMESTAMP}, #{content,jdbcType=LONGVARCHAR}
  )

mapper.update:
update t_article
set user_id = #{userId,jdbcType=INTEGER},
  cover_image = #{coverImage,jdbcType=VARCHAR},
  is_show = #{isShow,jdbcType=TINYINT},
  add_time = #{addTime,jdbcType=TIMESTAMP}
where article_id = #{articleId,jdbcType=INTEGER}
