mapper.select:
select

from t_user
where tel = #{tel, jdbcType=VARCHAR}

select

from t_user
where openid = #{openid, jdbcType=VARCHAR}

SELECT

from t_user


    and user_id = #{userId}


    and username = #{username}


    and tel = #{tel}


    and real_name = #{realName}


    and openid = #{openid}


    and age = #{age}


    and sex = #{sex}


    and id_card = #{idCard}


    and bank_card = #{bankCard}


    and address like #{address}


    and logins = #{logins}


    and alipay = #{alipay}


    and type = #{type}


    and status = #{status}


    and level = #{level}


    and body_status = #{bodyStatus}


    and headImgUrl = #{headImgUrl}


    and remark = #{remark}


order by user_id desc
